import React, { useState } from 'react'
import   Card   from './Card';
import './post.css';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import FavoriteIcon from '@material-ui/icons/Favorite';

function Post({post}) {
    const [ like, setLike ] = useState(post.like);
    const [ value, setValue ] = useState(false);
    const [ option, setOption ] = useState(false);
    const adding = () => {
        setLike(value? like - 1 : like + 1);
        setValue(!value);
    }
    return (
        
        <div className="statusContainer">
            <Card options = {option} />
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTop-left">
                        <img src={data.profile} alt="" className="postTopImg" />
                        <p className="heading">{data.user}</p>
                        <p className="timestamp">{data.timestamp}</p>
                    </div>
                    <MoreVertIcon onClick={()=> setOption(!option) } className="dotIcon" />
                </div>
                <div className="caption">
                    <p className="captionText">{data.caption}</p>
                </div>
                <div className="postImageContainer">
                    <img src={data.post} alt="" className="postImage"/>
                </div>
                <div className="postBottom">
                    <div className="postBottom-left">
                        <div className="likeIconBG">
                            <ThumbUpAltIcon onClick= { ()=> {
                                adding()}} className="likeIcon" />
                        </div>                       
                        <div onClick= { ()=> adding() } className="heartIconBG">
                            <FavoriteIcon className="heartIcon" />
                        </div>
                        <p className="likeDesc"><span className="likeNum"> {like} </span>people like it</p>
                    </div>
                    <p className="comments" ><span className="commentNum">{data.comment}</span>comments</p>
                </div>
            </div>
        </div>
    )
}

export default Post
